  let offc = r1.offsetHeight;
  r1.style.bottom = (0 - offc) + "px";
  r[0].style.opacity = "";
  let p = 0;
  let delta = 10;
  let id = setInterval(move_legend, delta);
  function move_legend() {
    p += delta;

    // console.log((0 - (offc * ( 1.0 - p / max))));
    if (p >= max)
      clearInterval(id);
    r1.style.bottom = (0 - (offc * (1.0 - p / max))) + "px";
  }
}

